import React from 'react';
// import data from '../data';

function InputSection() {

  let [memeNum, setMemeNum] = React.useState([1, 2]);


  function handleClick() {
    setMemeNum(prevState => [...prevState, `Thing ${prevState.length}`]);
    console.log(memeNum);
    // let randomNum = Math.floor(Math.random() * data.length);
    // setMemeNum(data[randomNum].url);

  };

  const newLine = memeNum.map(item => <p>{item}</p>);

  return (
    <main>
      <div className='form'>
        <input type='text' placeholder='Top line' ></input>
        <input type='text' placeholder='Bottom line'></input>
        <button onClick={handleClick}>Generate new image</button>
        {newLine}
        {/* <img src={memeNum}></img> */}
      </div>
    </main>
  );
};

export default InputSection;
