import React from 'react';
import data from '../data';

function InputSection() {

  // let [memeNum, setMemeNum] = React.useState('');

  const [meme, setMeme] = React.useState({
    topLine: 'TEST TOP',
    bottomLine: 'TEST BOTTOM',
    randomImage: 'http://i.imgflip.com/1bij.jpg'
  });

  const [allMemeImages, setAllMemeImages] = React.useState(data);

  function handleClick() {
    let randomNum = Math.floor(Math.random() * data.length);
    const url = data[randomNum].url;
    setMeme(prevState => ({
      ...prevState,
      randomImage: url
    }))
  };
  return (
    <main>
      <div className='form'>
        <input type='text' placeholder='Top line' ></input>
        <input type='text' placeholder='Bottom line'></input>
        <button onClick={handleClick}>Generate new image</button>
      </div>
      <div className='meme'>
          <div className='img-container'>
            <img src={meme.randomImage}></img>
          </div>
          <div className='text'>
            <h2 className='top-text'>{meme.topLine}</h2>
            <h2 className='bottom-text'>{meme.bottomLine}</h2>
          </div>
      </div>
    </main>
  );
};

export default InputSection;
