function InputSection() {
  const array = ['Thing 1', 'Thing2'];
  const newArray = array.map(item => <p>{item}</p>);
  // console.log(array);

  function handleClick() {
    array.push('Thing ' + array.length);

    console.log(array);
  }
  return (
    <main>
      <div className='form'>
        <input type='text' placeholder='Top line' ></input>
        <input type='text' placeholder='Bottom line'></input>
        <button onClick={handleClick} >Generate new image</button>
        {newArray}
      </div>
    </main>
  );
};

export default InputSection;


